import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

function Digit(props) {
  return (
    <button
      className="digit"
      onClick={props.onClick} >
      <h3>{props.value}</h3>
    </button>
  );
}

class Calculator extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      display: "0",
      digits: [
        7, 8, 9, '/',
        4, 5, 6, 'X',
        1, 2, 3, '-',
        0, '.', '=', '+',
      ],
      values: null,
    };
  }

  addOperator(i) {
    this.setState({
      display: null,
      values: this.state.values + `${i}`
    });
  }



  handleClick(i) {
    if (i in ['/', 'X', '-', '+']) {
      this.state.addOperator(i);
      return;
    } else if ( i === '=') {
      calcSum();
      return;
    } else {
      this.setState({
        display: i,
        values: `${i}`,
      });
    }
  }

  createDigit(i) {
    return (
      <Digit
        value={this.state.digits[i]}
        onClick={() => this.handleClick(i)}
      />
    );
  }

  createRow(start) {
    let digits = [];
    for (let i = start; i < start + 4; i++) {
      let newDigit = this.createDigit(i);
      digits.push(newDigit)
    }
    return digits
  }

  render() {
    return (
      <div className="calc-body">
        <div className="input-area">
          <h3>{this.state.display}</h3>
        </div>
        <div className="digit-row">
          {this.createRow(0)}
        </div>
        <div className="digit-row">
          {this.createRow(4)}
        </div>
        <div className="digit-row">
          {this.createRow(8)}
        </div>
        <div className="digit-row">
          {this.createRow(12)}
        </div>
      </div>
    )
  }
}

ReactDOM.render(<Calculator />, document.getElementById('root'))
